// src/App.js
import React from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import Home from './Home';   // Use the default import for Home
import Login from './Login'; // Use the default import for Login
import './App.css';

function App() {
  return (
    <Router>
      <div className="App">
        <Switch>
          <Route exact path="/" component={Home} />  {/* Display Home as the default page */}
          <Route path="/login" component={Login} />  {/* Login page route */}
        </Switch>
      </div>
    </Router>
  );
}

export default App;
